import com.eviware.soapui.model.testsuite.TestRunner.Status
def tc = testRunner.testCase.testSuite.project.testSuites["Customer Service Functional"].testCases["Property Expansion Customer"]
tc.testSteps["Email and Id Property"].setPropertyValue("Email","abiah112@gmail.com")
tc.run(null,false)
// checks what all the steps got executed
def teststeplist = tc.getTestStepList()
log.info teststeplist.size()

//shows the names of my test steps
teststeplist.each
{
	def tsname = it.name
	log.info tsname
}

def tc = testRunner.testCase.testSuite.project.testSuites["Customer Service Functional"].testCases["Property Expansion Customer"]
tc.testSteps["Email and Id Property"].setPropertyValue("Email","mariam@gmail.com")
def runner = tc.run(null,false)

def teststeplist = tc.getTestStepList()
//To see the names of test steps
teststeplist.each
{
	def tsname = it.name
	log.info tsname
}

log.info "Status: $runner.status"
//provide time taken for execution
log.info "TimeTaken: $runner.timeTaken"
//the number of steps executed
log.info "Number of Steps executed is :" + runner.results.size()

def validname = 6
def invalidname = 5
//Assert
if( runner.results.size()==6)
{
	log.info "The entered name is valid"
	assert runner.results.size() == validname
}
else
{
	log.info "The entered name is invalid"
	assert runner.results.size() == invalidname
}

